import React from "react";
import axios from "axios";

function Translate() {
  // async function translateToFrench(sentence) {
  // const API_KEY = "c2fc10e1-7f24-45b2-9cf7-ff69bc57d779";
  async function translateToFrench(sentence) {
    try {
      const response = await axios.post("https://api.libre.ai/translate", {
        source: "en",
        target: "fr",
        text: sentence,
      });
      return response.data.text;
    } catch (error) {
      console.error(error);
      return "Error occurred while translating the text.";
    }
  }

  return <div></div>;
}

export default Translate;
