import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./scan_result.scss";

const ScanResult = ({
  ocrState,
  confidence,
  intolerances,
  backButton,
  progress,
  loading,
}) => {
  const [matches, setMatches] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (!intolerances) {
      navigate("/login");
    }
  }, [intolerances, navigate]);

  const highlightText = (intolerances, str) => {
    if (!intolerances) {
      return;
    }

    // take out punctuation
    const validChars = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ ";
    const newStr = str
      .split("")
      .filter((char) => validChars.includes(char))
      .join("");

    // format inputs
    const intoleranceArray = intolerances.toLowerCase().split(", ");
    const strArray = newStr.toLowerCase().split(" ");
    const lowercaseOcrState = ocrState.array.map((index) =>
      index.toLowerCase()
    );

    // find matches
    const matches = strArray.filter((word) => intoleranceArray.includes(word));

    setMatches(matches);
  };

  const highlighterReturn = ocrState.array.map((word, j) => {
    const lowercaseWord = word.toLowerCase();
    const isMatch = matches.some((match) => lowercaseWord.includes(match));

    if (isMatch) {
      return (
        <>
          <span className="highlight">{word.toUpperCase()}</span>,&nbsp;
        </>
      );
    }

    return `${word} `;
  });

  return (
    <main className="results">
      <div className="text-box">
        <span className="results_main" id="inner">
          {/* {ocrState.text && <span> Results: {ocrState.text}</span>} */}
          {/* {confidence > 55 && <span>High confidence : {confidence}%</span>} */}
          <div className="text_with_highlight">
            {highlightText(intolerances, ocrState.text)}
          </div>
        </span>
      </div>

      {/* Scan new */}

      {matches && (
        <div className="navigation">
          <button
            type="button"
            className="btn btn-primary"
            onClick={backButton}
          >
            Scan again
          </button>
        </div>
      )}
    </main>
  );
};
