import React, { useState, useEffect, useNavigate } from "react";
import axios from "axios";
import "./EditProfile.scss";
import { useGlobalContext } from "../context";
import { handleCeliacCheck } from "../helpers/checkBoxHelpers";

const EditProfile = ({ ...props }) => {
  const [user, setUser] = useState(props.user || "");
  const { isLoggedIn, setIsLoggedIn } = useGlobalContext();
  let [storedData, setStoredData] = useState(
    JSON.parse(sessionStorage.getItem("userData"))
  );
  const celiacDiseaseChecked = event.target.checked;

  if (isLoggedIn) {
    storedData = JSON.parse(sessionStorage.getItem("userData"));
  }

  const updateUser = () => {
    fetch("/update", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        sessionData: storedData.data.user.id,
      }),
    });
    fetch("/update", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        sessionData: storedData.data.user.id,
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        sessionStorage.setItem("userData", JSON.stringify(data));
        setStoredData(data);
        props.toggle();
      })
      .catch((err) => console.error(err));
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h3>Edit Intolerances</h3>
        </div>
        <form method="POST" action="/register">
          <div className="row">
            <div className="col-md-6 mb-4"></div>
          </div>
          {/* Intolerances */}
          <div className="form-outline mb-4">
            <input
              id="intolerance--edit"
              placeholder="Please put any special ingredients here.."
              className="form-control"
              onChange={(event) =>
                setUser((prev) => ({
                  ...prev,
                  userPassword: event.target.value,
                }))
              }
            />
            <label className="form-label" for="password">
              Intolerances
            </label>
          </div>

          {/* List of premade intolerances */}
          <label className="form-label">
            Please select any of the pre-defined list of intolerances or
            allergies:
          </label>
          <div className="form-outline mb-4">
            <div class="form-check">
              <input
                class="form-check-input"
                type="checkbox"
                value=""
                onChange={handleCeliacCheck}
                id="flexCheckDefault"
              />
              <label class="form-check-label" for="flexCheckDefault">
                Celiac Disease - (Wheat, Rye, Barley)
              </label>
            </div>
            <div class="form-check">
              <input
                class="form-check-input"
                type="checkbox"
                value=""
                id="flexCheckDefault"
              />
              <label class="form-check-label" for="flexCheckDefault">
                Peanut Allergy
              </label>
            </div>
            <div class="form-check">
              <input
                class="form-check-input"
                type="checkbox"
                value=""
                id="flexCheckDefault"
              />
              <label class="form-check-label" for="flexCheckDefault">
                Vegetarian -(Beed, Pork, Chicken, Seafood)
              </label>
            </div>
            <div class="form-check">
              <input
                class="form-check-input"
                type="checkbox"
                value=""
                id="flexCheckDefault"
              />
              <label class="form-check-label" for="flexCheckDefault">
                Dairy-Free (Lactose, Milk, Cheese, Yogurt)
              </label>
            </div>
          </div>
        </form>
        <div className="modal-footer">
          <button
            type="submit"
            className="btn btn-primary"
            onClick={() => {
              updateUser();
              props.toggle();
            }}
          >
            Save Changes
          </button>
          <button onClick={props.toggle} className="btn btn-primary">
            Close
          </button>
        </div>
      </div>
    </div>
  );
};

export default EditProfile;
