import React, { useState, useEffect } from "react";
import EditProfile from "../componetns/EditProfile";
import axios from "axios";
import "./Profile.scss";
import { useGlobalContext } from "../context";

//Function that if the sting passed is greater than nth number of char, hides the remaining after 150 and adds ...
// function truncate(string, n) {
//   return string?.length > n ? string.substr(0, n - 1) + "..." : string;

const Profile = (props) => {
  const [showModal, setShowModal] = useState(false);
  const [intolerances, setIntolerances] = useState([]);
  const { isLoggedIn, setIsLoggedIn } = useGlobalContext();
  const [storedData, setStoredData] = useState(
    JSON.parse(sessionStorage.getItem("userData"))
  );

  useEffect(() => {
    setStoredData(JSON.parse(sessionStorage.getItem("userData")));
  }, []);

  const toggleModal = () => {
    setShowModal(!showModal);
  };

  axios
    .post("/intolerances", {
      sessionData: storedData.data.user.id,
    })
    .then((res) => {
      setIntolerances(res.data.rows[0].intolerance);
    })
    .catch((err) => console.error(err.response.data));

  async function checkGlutenFreeIngredients(ingredients) {
    const ingredientString = ingredients.join(",");
    const apiKey = "596c8cbb08394a8482c1a10627c3d85d";
    const apiUrl = `https://api.spoonacular.com/recipes/complexSearch?query=wheat&additionalRequirements=gluten-free&apiKey=${apiKey}`;

    try {
      const response = await axios.get(apiUrl);
      const results = response.data.results;
      const glutenFreeIngredients = [];
      const notGlutenFreeIngredients = [];
      console.log(response);
      for (const ingredient of ingredients) {
        let isGlutenFree = false;
        for (const result of results) {
          if (result.title.toLowerCase().includes(ingredient.toLowerCase())) {
            isGlutenFree = true;
            break;
          }
        }
        if (isGlutenFree) {
          glutenFreeIngredients.push(ingredient);
        } else {
          notGlutenFreeIngredients.push(ingredient);
        }
      }

      console.log(
        "Gluten-free ingredients: ",
        glutenFreeIngredients.join(", ")
      );
      console.log(
        "Not gluten-free ingredients: ",
        notGlutenFreeIngredients.join(", ")
      );
    } catch (error) {
      console.error(error);
    }
  }

  console.log(checkGlutenFreeIngredients([intolerances]));

  return (
    <section>
      <div className="card">
        <img
          className="card-img-top"
          src="https://t3.ftcdn.net/jpg/02/94/69/02/360_F_294690239_ydek2JMXB9aeQcYY05jVewc0xocZrCNg.jpg"
          alt="veg"
        />
        <div className="card-body">
          <h3 className="h2 text-black mb-0 user--name">{`${storedData.data.user.first_name} ${storedData.data.user.last_name}`}</h3>
        </div>
        <ul className="list-group list-group-flush">
          <li className="list-group-item">
            First Name: {storedData.data.user.first_name}
          </li>
          <li className="list-group-item">
            Last Name: {storedData.data.user.last_name}
          </li>
          <li className="list-group-item">
            Email: {storedData.data.user.email}
          </li>
          <li className="list-group-item">Intolerances: {intolerances}</li>
        </ul>
        <div className="card-body">
          <div>
            <button onClick={toggleModal} className="btn btn-primary">
              Edit
            </button>
          </div>

          {showModal && (
            <EditProfile
              toggle={toggleModal}
              state={(showModal, setShowModal)}
              storedData={storedData}
              setIntolerances={setIntolerances}
            />
          )}
        </div>
      </div>
    </section>
  );
};

export default Profile;
