import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Recipe.scss";
import { useResolvedPath } from "react-router-dom";

const Recipe = () => {
  const [recipes, setRecipes] = useState([]);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [ingredients, setIngredients] = useState([]);
  const [diet, setDiet] = useState("gluten-free");

  useEffect(() => {
    getRecipes();
  }, [diet]);

  const getRecipes = async () => {
    try {
      const response = await axios.get(
        `https://api.spoonacular.com/recipes/complexSearch?diet=${diet}&instructionsRequired=true&apiKey=596c8cbb08394a8482c1a10627c3d85d`
      );
      setRecipes(response.data.results);
    } catch (error) {
      console.error(error);
    }
  };

  const getRecipeDetails = async (recipeId) => {
    try {
      const response = await axios.get(
        `https://api.spoonacular.com/recipes/${recipeId}/analyzedInstructions?apiKey=596c8cbb08394a8482c1a10627c3d85d`
      );

      return response.data[0].steps.map((step) => step.step);
    } catch (error) {
      console.error(error);
    }
  };

  const getIngredients = async (recipeId) => {
    try {
      const response = await axios.get(
        `https://api.spoonacular.com/recipes/${recipeId}/ingredientWidget.json?apiKey=596c8cbb08394a8482c1a10627c3d85d`
      );
      console.log(response.data.ingredients);

      return response.data[0].ingredients.name.map((ingredient) =>
        setIngredients(ingredient.ingredient)
      );
    } catch (error) {
      console.error(error);
    }
  };

  const handleSelectRecipe = async (recipe) => {
    setSelectedRecipe(null);
    setSelectedRecipe({
      ...recipe,
      instructions: await getRecipeDetails(recipe.id),
      ingredients: await getIngredients(recipe.id),
    });
  };

  const handleChangeDiet = (event) => {
    setDiet(event.target.value);
  };

  // selectedRecipe ? console.log(ingredients) : console.log("");

  return (
    <div className="recipe-container">
      <h1>Random Recipe</h1>
      <select value={diet} onChange={handleChangeDiet}>
        <option value="gluten-free">Gluten-Free</option>
        <option value="vegetarian">Vegetarian</option>
        <option value="vegan">Vegan</option>
        <option value="paleo">Paleo</option>
      </select>
      <button
        disabled={recipes.length === 0}
        onClick={() =>
          handleSelectRecipe(
            recipes[Math.floor(Math.random() * recipes.length)]
          )
        }
      >
        Show Random Recipe
      </button>
      {selectedRecipe ? (
        <div>
          <div className="selected-recipe">
            <h2>{selectedRecipe.title}</h2>
            <img src={selectedRecipe.image} alt={selectedRecipe.title} />
            <h3>Instructions:</h3>
            <ol>
              {selectedRecipe.instructions.map((instruction, index) => (
                <li key={index}>{instruction}</li>
              ))}
            </ol>
          </div>
          {/* <div>
            <h3>Ingredients:</h3>
            <ol>
              {selectedRecipe.ingrdients.map((ingredient, index) => (
                <li key={index}>{ingredient}</li>
              ))}
            </ol>
          </div> */}
        </div>
      ) : (
        <p>No recipe selected.</p>
      )}
    </div>
  );
};
export default Recipe;
