import React from "react";
import axios from "axios";
import "./Translate.scss";

function Translate() {
  async function translateToFrench(sentence) {
    try {
      const response = await fetch("https://libretranslate.com/translate", {
        method: "POST",
        body: JSON.stringify({
          q: sentence,
          source: "en",
          target: "fr",
          format: "text",
          api_key: "c2fc10e1-7f24-45b2-9cf7-ff69bc57d779",
        }),
        headers: { "Content-Type": "application/json" },
      });
      const data = await response.json();
      console.log(data);
    } catch (error) {
      console.error(error);
      return "Error occurred while translating the text.";
    }
  }

  console.log(
    translateToFrench(
      "My name is Brad, I have Celiac Disease. I can not eat Wheat, Rye or Barley"
    )
  );

  return (
    <div>
      <section>
        <div className="container--card">
          <h1>Travel Cards</h1>
          <p>
            Here you can create a custom travel card that you can show resturant
            staff that may speak a different language than you.
          </p>
          <form action="">
            <input type="dropdown" />
          </form>
        </div>
      </section>
    </div>
  );
}

export default Translate;
