import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./scan_result.scss";

const ScanResult = ({
  ocrState,
  confidence,
  intolerances,
  backButton,
  progress,
  loading,
}) => {
  const [matches, setMatches] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (!intolerances) {
      navigate("/login");
    }
  }, [intolerances, navigate]);

  const highlightText = (intolerances, str) => {
    if (!intolerances) {
      return;
    }

    // take out punctuation
    const validChars = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ ";
    const newStr = str
      .split("")
      .filter((char) => validChars.includes(char))
      .join("");

    // format inputs
    const intoleranceArray = intolerances.toLowerCase().split(", ");
    const strArray = newStr.toLowerCase().split(" ");
    const lowercaseOcrState = ocrState.array.map((index) =>
      index.toLowerCase()
    );

    // find matches
    const matches = strArray.filter((word) => intoleranceArray.includes(word));

    setMatches(matches);
  };

  const highlighterReturn = ocrState.array.map((word, j) => {
    const lowercaseWord = word.toLowerCase();
    const isMatch = matches.some((match) => lowercaseWord.includes(match));

    if (isMatch) {
      return (
        <>
          <span className="highlight">{word.toUpperCase()}</span>,&nbsp;
        </>
      );
    }

    return `${word} `;
  });

  return (
    <>
      {matches.length > 0 && !loading && (
        <div className="result_matches">
          <div className="result_matches_warning">
            Results: Warning, this product contains harmful ingredients!
          </div>
          <div className="result_matches_text">{highlighterReturn}</div>
        </div>
      )}
      {matches.length === 0 && !loading && (
        <div className="result_nomatches">
          <div className="result_nomatches_warning">
            Results: This product is safe for consumption!
          </div>
          <div className="result_nomatches_text">
            {ocrState.array.join(" ")}
          </div>
        </div>
      )}
    </>
  );
};

export default ScanResult;
